package pers.coderaji.teez.config.annotation;import java.lang.annotation.ElementType;import java.lang.annotation.Retention;import java.lang.annotation.RetentionPolicy;import java.lang.annotation.Target;/** * @author aji * @description @Service * @date 2024/7/25 17:31 */@Target(ElementType.TYPE)@Retention(RetentionPolicy.RUNTIME)public @interface Service {	Class<?> type() default void.class;	String name() default "";	String version() default "";	String group() default "";}